package Week1.Day1;

public class IncreDecreOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		int a,b,c,d;
		a=++i;
		b=--i;
		c=i++;
		d=i--;
		System.out.println(a); 
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
	}

}
